script:
frappe.ui.form.on('Material Request', {
    refresh(frm) {
        handle_biometric_logic(frm);

        // Add warning message if biometric signature is missing
        if (frm.doc.docstatus === 1 && frm.doc.material_request_type === "Material Issue") {
            if (!has_biometric_signature(frm)) {
                frm.dashboard.add_comment(__('Please add a biometric signature before creating Material Issue.'), 'yellow');
            }
        }
    },

    material_request_type(frm) {
        handle_biometric_logic(frm);
    },

    validate(frm) {
        if (
            frm.doc.material_request_type === "Material Issue" &&
            frappe.flags.submit &&
            !has_biometric_signature(frm)
        ) {
            frappe.throw("Biometric Signature is required before submitting a Material Issue request.");
        }
    },

   // Modified make_stock_entry function to pass employee data
    make_stock_entry: function(frm) {
        if (frm.doc.material_request_type === "Material Issue" && !has_biometric_signature(frm)) {
            frappe.msgprint(__("Please add a biometric signature before creating Material Issue."));
            return;
        }

        // Get the employee from biometric data
        let employee = null;
        if (frm.doc.custom_biometric_data && frm.doc.custom_biometric_data.length > 0) {
            employee = frm.doc.custom_biometric_data[0].employee;
        }

        // If validation passes, open the mapped doc and set employee
        frappe.model.open_mapped_doc({
            method: "erpnext.stock.doctype.material_request.material_request.make_stock_entry",
            frm: frm,
            callback: function(r) {
                if (r.doc && employee) {
                    frappe.model.set_value(r.doc.doctype, r.doc.name, "employee", employee);
                }
            }
        });
    }
});

function handle_biometric_logic(frm) {
    frm.set_df_property("custom_biometric_data", "hidden", 1);
    frm.set_df_property("custom_biometric_data", "reqd", 0);
    frm.remove_custom_button('Add Biometric Signature');

    if (frm.doc.docstatus === 1 && frm.doc.material_request_type === "Material Issue") {
        frm.set_df_property("custom_biometric_data", "hidden", 0);
        frm.set_df_property("custom_biometric_data", "reqd", 1);
        if (!has_biometric_signature(frm)) {
            add_biometric_button(frm);
        }
    }
}

function has_biometric_signature(frm) {
    return (frm.doc.custom_biometric_data || []).length > 0;
}

function add_biometric_button(frm) {
    frm.add_custom_button('Add Biometric Signature', async () => {
        if (has_biometric_signature(frm)) {
            frappe.msgprint('Biometric data already added.');
            frm.remove_custom_button('Add Biometric Signature');
            return;
        }

        try {
            const result = await frappe.call({
                method: 'get_latest_biometric_log'
            });

            if (result && result.message) {
                const log = result.message;
                const row = frappe.model.add_child(frm.doc, 'Biometric Signature', 'custom_biometric_data');
                row.employee = log.employee;
                row.employee_name = log.employee_name;
                row.biometric_id = log.biometric_id;
                row.timestamp = log.timestamp;

                frm.refresh_field('custom_biometric_data');
                frappe.msgprint('Biometric data added.');
                frm.remove_custom_button('Add Biometric Signature');
            } else {
                frappe.msgprint('No biometric log found in the last 30 seconds.');
            }
        } catch (e) {
            frappe.msgprint(__('Error fetching biometric log: ') + e.message);
        }
    });
}
