script:
time_threshold = frappe.utils.add_to_date(frappe.utils.now_datetime(), minutes=-5000000)

latest_log = frappe.get_all(
    "Biometric Log",
    filters={
        "timestamp": [">", time_threshold]
    },
    fields=["employee", "biometric_id", "timestamp","employee_name"],
    order_by="timestamp desc",
    limit=1
)

if latest_log:
    frappe.response['message'] = latest_log[0]
else:
    frappe.throw("No biometric log found in the last 30 seconds.")
