script:
time_threshold = frappe.utils.add_to_date(frappe.utils.now_datetime(), minutes=-0.5)
latest_log = frappe.get_all(
    "Biometric Log", 
    filters={"timestamp": [">", time_threshold]}, 
    fields=["employee", "biometric_id", "timestamp", "employee_name"], 
    order_by="timestamp desc", 
    limit=1
)
if latest_log:
    frappe.response['message'] = latest_log[0]
else:
    frappe.response['message'] = {"error": "Please place finger on the Biometric Device"}
